{
  "name": "live2d-widget",
  "version": "0.9.0",
  "description": "Live2D widget for web pages",
  "main": "autoload.js",
  "type": "module",
  "scripts": {
    "build": "rollup -c rollup.config.js -f iife | terser -c -m > waifu-tips.js",
    "build-dev": "rollup -c rollup.config.js -f iife -o waifu-tips.js -w"
  },
  "repository": {
    "type": "git",
    "url": "git+https://gitee.com/dnest/live2d-widget.git"
  },
  "keywords": [
    "Live2d"
  ],
  "author": "stevenjoezhang <stevenjoezhang@gmail.com>",
  "license": "GPL-3.0-or-later",
  "bugs": {
    "url": "https://gitee.com/dnest/live2d-widget/issues"
  },
  "homepage": "https://gitee.com/dnest/live2d-widget#readme",
  "devDependencies": {
    "@fortawesome/fontawesome-free": "^6.2.0",
    "@rollup/plugin-node-resolve": "^15.0.0",
    "@rollup/pluginutils": "^5.0.1",
    "rollup": "^3.2.3",
    "terser": "^5.15.1"
  }
}

